import React from "react";
import useConversation from "../../stateManage/useConversation.js"
import {useSocketContext} from "../../context/SocketContext.jsx"
export default function user({user}) {
    const {selectedConversation, setSelectedConversation}=useConversation();
    const isSelected=selectedConversation?._id===user._id;
    const {socket, onlineUsers}=useSocketContext()
    const isOnline=onlineUsers.includes(user._id)
    return (
        <div className={`hover:bg-slate-100 duration-300 ${isSelected ? "bg-slate-900" : ""}`} onClick={()=>setSelectedConversation(user)}>
            <div className="flex space-x-4 px-8 py-3 hover:bg-slate-800 duration-300 cursor-pointer">
                <div className={`avatar ${isOnline ? "online" : ""}`}>
                    <div className="w-13 rounded-full">
                        <img src="data:image/jpeg;base64," className="rounded-full"
    style={{ width: "52px", height: "52px", objectFit: "cover", borderRadius: "50%" }} />
                    </div>
                </div>
                <div>
                    <h1 className="font-bold">{user.name}</h1>
                    <span>{user.email}</span>
                </div>
            </div>
        </div>
        
    );
}










