import React from "react";
import useConversation from "../../stateManage/useConversation.js"
import { useSocketContext } from "../../context/SocketContext.jsx";
export default function Chatuser() {
    const { onlineUsers}=useSocketContext()
    const {selectedConversation}=useConversation()
    const getOnlineUserStatus=(userId)=>{
        return onlineUsers.includes(userId)?"online":"offline";
    }
    return (
        <>
        <div className="px-3 py-1.5 h-[10.5vh] flex space-x-3 bg-gray-800 hover:bg-gray-700 duration-200 cursor-pointer">
           <div>
                {/* <div className={`avatar ${isOnline ? "online" : ""}`}> */}
                <div className="avatar online">
                    <div className="w-13 rounded-full">
                        {/* <img src="https://img.daisyui.com/images/profile/demo/gordon@192.webp" /> */}
                      
                      <img src="data:image/jpeg;base64," className="rounded-full"
    style={{ width: "52px", height: "52px", objectFit: "cover", borderRadius: "50%" }} />
                        {/* <img src="data:image/jpeg;base64," /> */}
                    </div>
                </div>
           </div>

           <div>
                <h1 className="text-xl">{selectedConversation.name}</h1>
                {/* <h1 className="text-xl">Adarsh</h1> */}
                <span className="text-sm">{getOnlineUserStatus(selectedConversation._id)}</span>
           </div>
        </div>
        </>
    );
}











